package com.sordi.userManagement.config;

import com.sordi.userManagement.security.CustomUserDetailsService;
import com.sordi.userManagement.security.JwtAuthenticationFilter;
import com.sordi.userManagement.security.JwtTokenProvider;
import lombok.RequiredArgsConstructor;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.config.annotation.authentication.configuration.AuthenticationConfiguration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.config.annotation.web.configurers.AbstractHttpConfigurer;
import org.springframework.security.config.annotation.web.configurers.HeadersConfigurer;
import org.springframework.security.config.http.SessionCreationPolicy;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.web.authentication.UsernamePasswordAuthenticationFilter;

/**
 * Configuración de seguridad para la aplicación.
 * Define beans de seguridad, reglas de autenticación y autorización.
 */
@Configuration
@EnableWebSecurity
@RequiredArgsConstructor
public class SecurityConfig {

    // Dependencias necesarias para la configuración de seguridad
    private final CustomUserDetailsService customUserDetailsService;
    private final JwtTokenProvider jwtTokenProvider;

    /**
     * Bean para el codificador de contraseñas.
     */
    @Bean
    public PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }

    /**
     * Bean para el AuthenticationManager.
     */
    @Bean
    public AuthenticationManager authenticationManager(
            AuthenticationConfiguration authConfig) throws Exception {
        return authConfig.getAuthenticationManager();
    }

    /**
     * Bean de SecurityFilterChain - Define las reglas de seguridad de la aplicación.
     */
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http
            // Deshabilitar Cross-Site Request Forgery (CSRF)
            .csrf(AbstractHttpConfigurer::disable)

            // Configurar sesiones como STATELESS (para JWT)
            // No guardamos estado en el servidor
            .sessionManagement(session ->
                session.sessionCreationPolicy(SessionCreationPolicy.STATELESS))

            // Configurar qué endpoints requieren autenticación
            .authorizeHttpRequests(auth -> auth
                // ENDPOINTS PÚBLICOS (sin autenticación)
                .requestMatchers("/api/auth/**").permitAll()        // Login, register
                .requestMatchers("/h2-console/**").permitAll()         // Base de datos H2 - solo desarrollo

                // ENDPOINTS OPCIONALES
                .requestMatchers("/swagger-ui/**").permitAll()         //  Documentación Swagger
                .requestMatchers("/v3/api-docs/**").permitAll()        //  Datos para Swagger
                .requestMatchers("/actuator/**").permitAll()           //  Monitoreo de aplicación

                // ENDPOINTS PRIVADOS (requieren autenticación)
                .anyRequest().authenticated()
            )

            /**
             * Configurar el filtro de autenticación JWT.
             * Este filtro intercepta las solicitudes y verifica el token JWT.
             * Si el token es válido, permite el acceso al recurso solicitado
             */
            .addFilterBefore(jwtAuthenticationFilter(), UsernamePasswordAuthenticationFilter.class)

            // Configurar headers para H2 console (solo desarrollo)
            // H2 usa frames y Spring Security los bloquea por defecto
            .headers(headers -> headers
                .frameOptions(HeadersConfigurer.FrameOptionsConfig::disable)
            );

        return http.build();
    }
    @Bean
    public JwtAuthenticationFilter jwtAuthenticationFilter() {
        return new JwtAuthenticationFilter(jwtTokenProvider,customUserDetailsService);}


}

package com.sordi.userManagement.config;

import com.sordi.userManagement.security.CustomUserDetailsService;
import com.sordi.userManagement.security.JwtAuthenticationFilter;
import com.sordi.userManagement.security.JwtTokenProvider;
import lombok.RequiredArgsConstructor;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.authentication.AuthenticationManager;
import org.springframework.security.config.annotation.authentication.configuration.AuthenticationConfiguration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.config.annotation.web.configurers.AbstractHttpConfigurer;
import org.springframework.security.config.annotation.web.configurers.HeadersConfigurer;
import org.springframework.security.config.http.SessionCreationPolicy;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.crypto.password.PasswordEncoder;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.web.authentication.UsernamePasswordAuthenticationFilter;

/**
 * Configuración de seguridad para la aplicación.
 * Define beans de seguridad, reglas de autenticación y autorización.
 */
@Configuration
@EnableWebSecurity
@RequiredArgsConstructor
public class SecurityConfig {

    // Dependencias necesarias para la configuración de seguridad
    private final CustomUserDetailsService customUserDetailsService;
    private final JwtTokenProvider jwtTokenProvider;

    /**
     * Bean para el codificador de contraseñas.
     */
    @Bean
    public PasswordEncoder passwordEncoder() {
        return new BCryptPasswordEncoder();
    }

    /**
     * Bean para el AuthenticationManager.
     */
    @Bean
    public AuthenticationManager authenticationManager(
            AuthenticationConfiguration authConfig) throws Exception {
        return authConfig.getAuthenticationManager();
    }

    /**
     * Bean de SecurityFilterChain - Define las reglas de seguridad de la aplicación.
     */
    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http
            // Deshabilitar Cross-Site Request Forgery (CSRF)
            .csrf(AbstractHttpConfigurer::disable)

            // Configurar sesiones como STATELESS (para JWT)
            // No guardamos estado en el servidor
            .sessionManagement(session ->
                session.sessionCreationPolicy(SessionCreationPolicy.STATELESS))

            // Configurar qué endpoints requieren autenticación
            .authorizeHttpRequests(auth -> auth
                // ENDPOINTS PÚBLICOS (sin autenticación)
                .requestMatchers("/api/auth/**").permitAll()        // Login, register
                .requestMatchers("/h2-console/**").permitAll()         // Base de datos H2 - solo desarrollo

                // ENDPOINTS OPCIONALES
                .requestMatchers("/swagger-ui/**").permitAll()         //  Documentación Swagger
                .requestMatchers("/v3/api-docs/**").permitAll()        //  Datos para Swagger
                .requestMatchers("/actuator/**").permitAll()           //  Monitoreo de aplicación

                // ENDPOINTS PRIVADOS (requieren autenticación)
                .anyRequest().authenticated()
            )

            // Configurar el filtro de autenticación JWT
            // Este filtro intercepta las solicitudes y verifica el token JWT
            // Si el token es válido, permite el acceso al recurso solicitado
            .addFilterBefore(jwtAuthenticationFilter(), UsernamePasswordAuthenticationFilter.class)

            // Configurar headers para H2 console (solo desarrollo)
            // H2 usa frames y Spring Security los bloquea por defecto
            .headers(headers -> headers
                .frameOptions(HeadersConfigurer.FrameOptionsConfig::disable)
            );

        return http.build();
    }
    @Bean
    public JwtAuthenticationFilter jwtAuthenticationFilter() {
        return new JwtAuthenticationFilter(jwtTokenProvider,customUserDetailsService);}


}

package com.sordi.userManagement.model.dto.request;
 * DTO para actualización de usuario por parte del administrador.
 * Incluye campos que solo admin puede modificar como isActive.
 */
@Data
@Builder
@NoArgsConstructor
@AllArgsConstructor
@Schema(description = "Request para actualización de usuario por administrador")
public class AdminUpdateUserRequest {

    @Schema(description = "Nombre del usuario", example = "Juan")
    @Size(min = 2, max = 50, message = "El nombre debe tener entre 2 y 50 caracteres")
    private String firstName;

    @Schema(description = "Apellido del usuario", example = "Pérez")
    @Size(min = 2, max = 50, message = "El apellido debe tener entre 2 y 50 caracteres")
    private String lastName;

    @Schema(description = "Email del usuario", example = "juan.perez@email.com")
    @Email(message = "Formato de email inválido")
    private String email;

    @Schema(description = "Estado activo del usuario", example = "true")
    private Boolean isActive; // ✅ Solo admin puede cambiar esto

    @Schema(description = "Rol del usuario", example = "USER")
    private String role; // ✅ Solo admin puede cambiar roles
}

package com.sordi.userManagement.model.dto.request;

import jakarta.validation.constraints.Email;
import jakarta.validation.constraints.NotBlank;
import jakarta.validation.constraints.Past;
import jakarta.validation.constraints.Size;

public class CreateUser {

    @NotBlank(message = "First name is required")
    @Size(min = 3, max = 50)
    private String firstName;

    @NotBlank(message = "Last name is required")
    @Size(min = 3, max = 50)
    private String lastName;

    @Past(message = "Date of birth must be in the past")
    private String dateOfBirth; // Format: YYYY-MM-DD

    @NotBlank(message = "DNI is required")
    @Size(min = 7, max = 10, message = "DNI must be between 7 and 10 characters")
    private String dni;

    @Email
    @Size(max = 100, message = "Email must be less than 100 characters")
    private String email;

    @NotBlank(message = "Username is required")
    @Size(min = 3, max = 50, message = "Username must be between 3 and 50 characters")
    private String username;

    @NotBlank(message = "Password is required")
    @Size(min = 6, max = 100, message = "Password must be between 6 and 100 characters")
    private String password;

}

package com.sordi.userManagement.model.dto.request;

import jakarta.validation.constraints.Email;
import jakarta.validation.constraints.NotBlank;
import jakarta.validation.constraints.NotNull;
import jakarta.validation.constraints.Past;
import jakarta.validation.constraints.Pattern;
import jakarta.validation.constraints.Size;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;

import java.time.LocalDate;

/**
 * DTO for creating a new user.
 * Contains all required information for user registration.
 * 
 * @author Santiago Sordi
 * @version 1.0
 */
@Data
@NoArgsConstructor
@AllArgsConstructor
public class CreateUserRequest {

    @NotBlank(message = "First name is required")
    @Size(min = 2, max = 50, message = "First name must be between 2 and 50 characters")
    @Pattern(regexp = "^[a-zA-ZáéíóúÁÉÍÓÚñÑ\\s]+$", message = "First name can only contain letters and spaces")
    private String firstName;

    @NotBlank(message = "Last name is required")
    @Size(min = 2, max = 50, message = "Last name must be between 2 and 50 characters")
    @Pattern(regexp = "^[a-zA-ZáéíóúÁÉÍÓÚñÑ\\s]+$", message = "Last name can only contain letters and spaces")
    private String lastName;

    @NotNull(message = "Date of birth is required")
    @Past(message = "Date of birth must be in the past")
    private LocalDate dateOfBirth;

    @NotBlank(message = "DNI is required")
    @Pattern(regexp = "^[0-9]{7,10}$", message = "DNI must be between 7 and 10 digits")
    private String dni;

    @NotBlank(message = "Email is required")
    @Email(message = "Invalid email format")
    @Size(max = 100, message = "Email must be less than 100 characters")
    private String email;

    @NotBlank(message = "Username is required")
    @Size(min = 3, max = 30, message = "Username must be between 3 and 30 characters")
    @Pattern(regexp = "^[a-zA-Z0-9_]+$", message = "Username can only contain letters, numbers and underscores")
    private String username;

    @NotBlank(message = "Password is required")
    @Size(min = 8, max = 100, message = "Password must be between 8 and 100 characters")
    @Pattern(
        regexp = "^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[@$!%*?&])[A-Za-z\\d@$!%*?&]+$",
        message = "Password must contain at least one lowercase letter, one uppercase letter, one digit and one special character"
    )
    private String password;
}

